import React from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import MerchandisePage from "../merchandise/merchandise.component";

import ShopOverview from "../../components/shop-overview/shop-overview.component";

import { fetchMerchandiseStartAsync } from "../../redux/shop/shop.actions.js";
import {
  selectIsMerchandiseFetching,
  selectIsMerchandiseLoaded,
} from "../../redux/shop/shop.selectors";

import WithSpinner from "../../components/with-spinner/with-spinner.component";

const ShopOverviewWithSpinner = WithSpinner(ShopOverview);
const MerchandisePageWithSpinner = WithSpinner(MerchandisePage);

class Shop extends React.Component {
  componentDidMount() {
    const { fetchMerchandiseStartAsync } = this.props;
    fetchMerchandiseStartAsync();
  }
  render() {
    const { match, isMerchandiseLoaded } = this.props;
    return (
      <div>
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <ShopOverviewWithSpinner
              isLoading={!isMerchandiseLoaded}
              {...props}
            />
          )}
        />
        <Route
          path={`${match.path}/:merchandiseId`}
          render={(props) => (
            <MerchandisePageWithSpinner
              isLoading={!isMerchandiseLoaded}
              {...props}
            />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isMerchandiseFetching: selectIsMerchandiseFetching,
  isMerchandiseLoaded: selectIsMerchandiseLoaded,
});

const mapDispatchToProps = (dispatch) => ({
  fetchMerchandiseStartAsync: () => dispatch(fetchMerchandiseStartAsync()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Shop);
