import React from "react";

import "./sign-up.styles.scss";

import PersonalizedButton from "../personalized-button/personalized-button.component";
import CustomFormInput from "../custom-form-input/custom-form-input.component";

import { auth, createUserProfileDocument } from "../../firebase/firebase.utils";

class SignUp extends React.Component {
  constructor() {
    super();

    this.state = {
      email: "",
      password: "",
      confirmPassword: "",
      message: "",
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { email, password, confirmPassword } = this.state;

    if (password !== confirmPassword) {
      this.setState({
        email: "",
        password: "",
        confirmPassword: "",
        message: "Passwords don't match!",
      });
      return;
    }

    try {
      const { user } = await auth.createUserWithEmailAndPassword(
        email,
        password
      );
      await createUserProfileDocument(user, { email });
    } catch (error) {
      console.log(error);
    }
  };
  render() {
    const { email, password, confirmPassword } = this.state;
    return (
      <div className="sign-up">
        <h2>Sign Up</h2>
        <form onSubmit={this.handleSubmit}>
          <CustomFormInput
            type="email"
            name="email"
            value={email}
            label="Email"
            onChange={this.handleChange}
          />
          <CustomFormInput
            type="password"
            name="password"
            value={password}
            label="Password"
            onChange={this.handleChange}
          />
          <CustomFormInput
            type="password"
            name="confirmPassword"
            value={confirmPassword}
            label="Confirm Password"
            onChange={this.handleChange}
          />
          <PersonalizedButton type="submit">Sign Up</PersonalizedButton>
          {this.state.message}
        </form>
      </div>
    );
  }
}

export default SignUp;
