import React from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import ShopItem from "../../components/shop-item/shop-item.component";

import { selectMerchandiseForPreview } from "../../redux/shop/shop.selectors";
import { updateMerchandise } from "../../redux/shop/shop.actions";

import {
  firestore,
  convertCollectionSnapshotToMap,
} from "../../firebase/firebase.utils";

class Shop extends React.Component {
  unsubscribeFromSnapshot = null;
  componentDidMount() {
    const { updateMerchandise } = this.props;
    const merchandiseRef = firestore.collection("merchandise");
    merchandiseRef.onSnapshot(async (snaphot) => {
      const merchandiseMap = convertCollectionSnapshotToMap(snaphot);
      updateMerchandise(merchandiseMap);
      console.log(merchandiseMap);
    });
  }

  render() {
    const { id, label, items } = this.props.merchandise;
    return (
      <div>
        {merchandise.map(({ id, label, items }) => (
          <div key={id}>
            <ShopItem label={label} items={items} />
          </div>
        ))}
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  merchandise: selectMerchandiseForPreview,
});

const mapDispatchToProps = (dispatch) => ({
  updateMerchandise: (merchandiseMap) =>
    dispatch(updateMerchandise(merchandiseMap)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Shop);
