import React from "react";

import "./sign-in.styles.scss";

import PersonalizedButton from "../personalized-button/personalized-button.component";
import CustomFormInput from "../custom-form-input/custom-form-input.component";

import { auth, signInWithGoogle } from "../../firebase/firebase.utils";

class SignIn extends React.Component {
  constructor() {
    super();

    this.state = {
      email: "",
      password: "",
    };
  }

  handleChange = (event) => {
    const { value, name } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { email, password } = this.state;

    try {
      await auth.signInWithEmailAndPassword(email, password);
      this.setState({
        email: "",
        password: "",
      });
    } catch (error) {
      console.log(error);
    }
  };

  render() {
    return (
      <div className="sign-in">
        <form>
          <CustomFormInput
            name="email"
            type="email"
            value={this.state.email}
            label="email"
            onChange={this.handleChange}
            required
          />
          <CustomFormInput
            name="password"
            type="password"
            value={this.state.password}
            label="password"
            onChange={this.handleChange}
            required
          />

          <div className="buttons">
            <PersonalizedButton type="submit">Sign In</PersonalizedButton>
            <PersonalizedButton
              type="button"
              onClick={signInWithGoogle}
              googleSignIn
            >
              Sign In with Google
            </PersonalizedButton>
          </div>
        </form>
      </div>
    );
  }
}

export default SignIn;
